import React, { createContext, useContext, useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // V√©rifier la session au chargement
  useEffect(() => {
    checkUser();

    // √âcouter les changements d'authentification
    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (event, session) => {
      console.log('Auth event:', event);

      if (event === 'SIGNED_IN') {
        setUser(session?.user ?? null);
        console.log('‚úÖ Utilisateur connect√©:', session?.user?.email);
      } else if (event === 'SIGNED_OUT') {
        setUser(null);
        console.log('üëã Utilisateur d√©connect√©');
      } else if (event === 'TOKEN_REFRESHED') {
        console.log('üîÑ Token rafra√Æchi');
      }

      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  // V√©rifier l'utilisateur actuel
  const checkUser = async () => {
    try {
      console.log('üîç V√©rification de la session...');

      const { data: { session }, error } = await supabase.auth.getSession();

      if (error) {
        console.error('‚ùå Erreur v√©rification session:', error);
        throw error;
      }

      if (session) {
        console.log('‚úÖ Session trouv√©e:', session.user.email);
        setUser(session.user);
      } else {
        console.log('‚ÑπÔ∏è Aucune session active');
        setUser(null);
      }
    } catch (error) {
      console.error('‚ùå Erreur v√©rification session:', error);
      setUser(null);
    } finally {
      setLoading(false);
    }
  };

  // Connexion avec identifiant et mot de passe
  const login = async (username, password) => {
    try {
      console.log('üîê Tentative de connexion pour:', username);

      // L'email fictif est construit : username@booktime.local
      const email = `${username}@booktime.local`;

      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        console.error('‚ùå Erreur de connexion:', error.message);
        throw error;
      }

      console.log('‚úÖ Connexion r√©ussie:', data.user.email);
      setUser(data.user);

      return { success: true, user: data.user };
    } catch (error) {
      console.error('‚ùå Erreur de connexion:', error);

      let errorMessage = error.message;

      if (error.message === 'Invalid login credentials') {
        errorMessage = 'Identifiant ou mot de passe incorrect';
      } else if (error.message?.includes('Email not confirmed')) {
        errorMessage = 'Email non confirm√©';
      }

      return { 
        success: false, 
        error: errorMessage
      };
    }
  };

  // D√©connexion
  const logout = async () => {
    try {
      console.log('üëã D√©connexion en cours...');

      const { error } = await supabase.auth.signOut();

      if (error) throw error;

      setUser(null);
      console.log('‚úÖ D√©connexion r√©ussie');

      return { success: true };
    } catch (error) {
      console.error('‚ùå Erreur de d√©connexion:', error);
      return { success: false, error: error.message };
    }
  };

  const value = {
    user,
    loading,
    login,
    logout,
    checkUser
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
