import { createClient } from '@supabase/supabase-js';

// ðŸ”‘ REMPLACEZ CES VALEURS PAR LES VÃ”TRES
const supabaseUrl = 'https://VOTRE_PROJET.supabase.co';
const supabaseAnonKey = 'VOTRE_ANON_KEY';

export const supabase = createClient(supabaseUrl, supabaseAnonKey);

// Helper pour gÃ©rer les erreurs
export const handleSupabaseError = (error) => {
  console.error('Supabase error:', error);
  return {
    success: false,
    error: error.message || 'Une erreur est survenue'
  };
};

// Helper pour les rÃ©ponses rÃ©ussies
export const handleSupabaseSuccess = (data) => {
  return {
    success: true,
    data
  };
};
